	  				CATEGORYS = #{categorys}, BUSINESS = #{business}, NO_OF_EMPLOYEES = #{noOfEmployees}, CITY = #{city}
	  				ITEMS = #{items},NAMES = #{names}

WHERE STARTUP_ID = #{startupId}
